    private void jButton1MousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MousePressed
        senhaDigitada = txtSenhaDigitada.getText();
        
        UsuarioDAO dAO = new UsuarioDAO();//Faz com que o método "carregarNomeCandidato" reconheça 
                                            //que está chamando o método pesquisarCandidatoPorNumero
                                            //dentro da classe CandidatoDAO usando o parâmetro rs que está sendo retornado
        ResultSet rs = null;        
        
       rs = dAO.pesquisarUsuarioPorNome(txtLogin.getText());
       
       carregarDadosUsuario(rs);        
                
        if(senhaDigitada.equals(senhaFiltrada)){
            MenuVIEW menuView = new MenuVIEW();
            menuView.setVisible(true);            
            dispose();
            Usuario.nivel = nivelFiltrado;
        }
else{
    JOptionPane.showMessageDialog(null, "Login ou senha inválido !");
}
        
        
        
    }//GEN-LAST:event_jButton1MousePressed

