    public void carregarDadosUsuario(ResultSet rs) {
              
        try {           
            if (rs.next()) {                 
                String linha[] = new String[]{                    
                    rs.getString("codigo"),
                    rs.getString("senha"),
                    rs.getString("nivel")
                };
              codigoFiltrado = (rs.getInt("codigo"));
              senhaFiltrada = (rs.getString("senha"));
              nivelFiltrado = (rs.getString("nivel"));
              
                   }                       
        } 
        catch (Exception e) {
            System.out.println("Erro ao carregar nome: " + e.getMessage());            
        }       
    }
    
        
